mapper.select:
SELECT * FROM companies



            OR name LIKE '%${name}%'


            OR representative LIKE '%${representative}%'


            OR prefecture_id = #{prefectureId}


    AND deleted is NULL

;

mapper.insert:
INSERT INTO companies (user_id, name, representative, phone_number, postal_code, prefecture_code, address, mail_address, created, modified) VALUES

    (1, #{company.name}, #{company.representative}, #{company.phoneNumber}, #{company.postalCode}, #{company.prefectureCode}, #{company.address}, #{company.mailAddress}, NOW(), NOW())

;

mapper.update:
UPDATE companies
SET name = #{name}, representative = #{representative}, phone_number = #{phoneNumber}, postal_code = #{postalCode}, prefecture_code = #{prefectureCode}, address = #{address}, mail_address = #{mailAddress}, modified = NOW()
WHERE id = #{id};
